[asy]
pair P,Q,R,SS,T;
Q = (0,0);
R = (1,0);
P = (1.1,0.5);
SS = 0.6*P;
T = R + 0.6*(P-R);
draw(T--SS--P--R--Q--SS);
label("$P$",P,N);
label("$S$",SS,NW);
label("$Q$",Q,S);
label("$R$",R,S);
label("$T$",T,ENE);
[/asy]
